


class Brute_Solution(object):
    def findLength(self, nums1, nums2):
        """
        :type nums1: List[int]
        :type nums2: List[int]
        :rtype: int
        """
        output = 0
        
        for i in range(len(nums1)):
            for j in range(len(nums2)):
                max_len = 0
                while(True):
                    if i==len(nums1) or j==len(nums2):
                        break
                    if nums1[i]==nums2[j]:
                        max_len+=1
                        i+=1
                        j+=1
                    else: break
                output=max(output, max_len)
        return output



class Solution(object):
    def findLength(self, nums1, nums2):
        if nums1==nums2:
            return len(nums1)
        m, n = len(nums1), len(nums2)
        dp =  [[0]*(n+1) for _ in range(m+1)]
        ### backward form
        for i in range(m-1, -1, -1):
            for j in range(n-1, -1, -1):
                if nums1[i]==nums2[j]:
                    dp[i][j] = dp[i+1][j+1] + 1
        ### forward form
        # for i in range(m):
        #     for j in range(n):
        #         if nums1[i]==nums2[j]:
        #             dp[i][j] = dp[i-1][j-1] + 1
        return max(max(row) for row in dp)


